import { Post } from '../lib/api';
import Article from './Article';

interface CategoryProps {
    posts: Post[];
    title: string;
    setTagSelected: (tag: string) => void;
}

const Category = ({posts, title, setTagSelected}: CategoryProps) => {
    return (
        <>
            <div>{title}</div>
            <div>
                {posts.map((post) => {
                    return (
                        <Article post={post} setTagSelected={setTagSelected} />
                    );
                })}
            </div>
        </>
    );
};

export default Category;
