import { getAllPosts } from "../lib/api";
import { HomePage } from "../lib/styles/homePage";

const Home = ({ posts, postsByTags }) => {
    console.log(posts, postsByTags);
    return (
        <HomePage>
            <div>
                {Object.keys(postsByTags).map((tag) => {
                    return (
                        <div>
                            {tag}
                            {postsByTags[tag].map((post) => {
                                return (
                                    <span>{post.title}</span>
                                );
                            })}
                        </div>
                    );
                })}
            </div>
            <div>Content</div>
        </HomePage>
    );
};

// eslint-disable-next-line @typescript-eslint/require-await
export async function getStaticProps() {
    const posts = getAllPosts([
        'title',
        'slug',
        'author',
        'tags',
    ]);
    const postsByTags = {};
    posts.forEach((post) => {
        post.tags?.forEach((tag) => {
            if (Array.isArray(postsByTags[tag])) {
                postsByTags[tag].push(post);
            }
            else {
                postsByTags[tag] = [post];
            }
        });
    });
    return {
        props: {
            posts,
            postsByTags
        }
    };
}

export default Home;
