import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import { useState } from "react";
import { getAllPosts, Post } from "../lib/api";
import Article from '../components/Article';
import Category from '../components/Category';

const useStyles = makeStyles((theme) => ({
    root: {
        minWidth: 275,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    topBar: {
        marginBottom: 30,
    },
    toolBar: {
        display: 'flex',
        justifyContent: 'center'
    },
    tagButton: {
        marginRight: 15,
    },
}));

const Home = ({ posts, tags }) => {
    const [tagSelected, setTagSelected] = useState<string>();
    const classes = useStyles();
    return (
        <>
            <AppBar position="static" className={classes.topBar}>
                <Toolbar className={classes.toolBar}>
                    {tags.map(tag => (
                        <Button
                            className={classes.tagButton}
                            variant="contained"
                            key={tag}
                            onClick={() => setTagSelected(tag)}
                        >
                            {tag}
                        </Button>
                    ))}
                    <Button
                        className={classes.tagButton}
                        variant="contained"
                        key="all"
                        onClick={() => setTagSelected(undefined)}
                    >
                        Tout
                    </Button>
                </Toolbar>
            </AppBar>
            <Grid item xs={12}>
                <Grid container justify="center" spacing={2}>
                    {tags.map((tag: string) => {
                        return (
                            // eslint-disable-next-line @typescript-eslint/no-unsafe-return
                            <Category posts={posts.filter(post => post.tags.includes(tag))} title={tag} setTagSelected={setTagSelected} />
                        );
                    })}
                    {posts.filter((post: Post) => {
                        // eslint-disable-next-line @typescript-eslint/no-unsafe-return
                        return tagSelected === undefined || post.tags?.includes(tagSelected);
                    })
                        .map((post: Post) => (
                            <Article post={post} setTagSelected={ setTagSelected } />
                        ))}
                </Grid>
            </Grid>
        </>
    );
};

// eslint-disable-next-line @typescript-eslint/require-await
export async function getStaticProps() {
    const posts = getAllPosts([
        'title',
        'slug',
        'author',
        'tags',
        'summary',
    ]);
    const tags = new Set();
    posts.forEach((post) => {
        post.tags?.forEach((tag) => {
            tags.add(tag);
        });
    });
    return {
        props: {
            posts,
            tags: Array.from(tags),
        }
    };
}

export default Home;
